    val adapter = MyAdapter(libelle, this)

    override fun onClick(view: View) {
        if (view.tag != null){
            val position = view.tag as Int
            val etablissement = DataEtablissement(uai[position],libelle[position],type[position], secteur[position], commune[position],academie[position], adresse[position], zipCode[position])
            val intent = Intent(this.requireContext(), DetailActivity::class.java)
            intent.putExtra("etablissement", etablissement)
            startActivity(intent)
        }
    }

    override fun onCreateView(inflater: LayoutInflater,container: ViewGroup?,savedInstanceState: Bundle?): View? {
        val view = inflater.inflate(R.layout.list_fragment, container, false)

        val recyclerView = view.findViewById<RecyclerView>(R.id.item_recycler_view)
        recyclerView.layoutManager = LinearLayoutManager(this.requireContext())
        recyclerView.adapter = adapter

        return view
    }
